import{_ as r,d as a}from"./app.9843a57b.js";var i="/Xray-docs-next/assets/ch01-img01-choice.8b5c938e.png";const e={},p=a('<h1 id="\u3010\u7B2C-1-\u7AE0\u3011-\u5C0F\u5C0F\u767D\u767D\u8BDD\u6587" tabindex="-1"><a class="header-anchor" href="#\u3010\u7B2C-1-\u7AE0\u3011-\u5C0F\u5C0F\u767D\u767D\u8BDD\u6587" aria-hidden="true">#</a> \u3010\u7B2C 1 \u7AE0\u3011 \u5C0F\u5C0F\u767D\u767D\u8BDD\u6587</h1><h2 id="_1-1-\u8FD9\u7BC7\u6587\u6863\u662F\u5199\u7ED9\u8C01\u7684" tabindex="-1"><a class="header-anchor" href="#_1-1-\u8FD9\u7BC7\u6587\u6863\u662F\u5199\u7ED9\u8C01\u7684" aria-hidden="true">#</a> 1.1 \u8FD9\u7BC7\u6587\u6863\u662F\u5199\u7ED9\u8C01\u7684\uFF1F</h2><p>\u4E00\u53E5\u8BDD\uFF1A\u5199\u7ED9 <strong>\u2460 \u96F6\u57FA\u7840</strong> <strong>\u2461 \u5E0C\u671B\u5B66\u4E60\u81EA\u5EFA VPS</strong> \u7684\u65B0\u4EBA\u3002</p><h2 id="_1-2-\u8FD9\u7BC7\u6587\u6863\u4E0D\u662F\u5199\u7ED9\u8C01\u7684" tabindex="-1"><a class="header-anchor" href="#_1-2-\u8FD9\u7BC7\u6587\u6863\u4E0D\u662F\u5199\u7ED9\u8C01\u7684" aria-hidden="true">#</a> 1.2 \u8FD9\u7BC7\u6587\u6863\u4E0D\u662F\u5199\u7ED9\u8C01\u7684\uFF1F</h2><p>\u5305\u62EC\u4F46\u4E0D\u9650\u4E8E\uFF1A\u5404\u8DEF\u5927\u795E\u5927\u80FD\u3001\u61D2\u5F97\u81EA\u5DF1\u6298\u817E\u7684\u5C0F\u767D\u3001\u5DF2\u7ECF\u4F1A\u6298\u817E\u7684\u9AD8\u624B\u3001\u786E\u5B9A\u8981\u7528\u673A\u573A\u7684\u571F\u8C6A\u3001\u786E\u5B9A\u8981\u7528\u4E00\u952E\u811A\u672C\u7684\u900D\u9065\u6D3E...... \u603B\u4E4B\u53EA\u8981\u6709\u6280\u672F\u57FA\u7840\u3001\u6216\u4E0D\u613F\u4E0D\u60F3\u81EA\u5EFA\u7684\u540C\u5B66\uFF0C\u60A8\u76F4\u63A5\u5173\u95ED\u672C\u6587\u5373\u53EF\uFF0C\u56E0\u4E3A\u8FD9\u7BC7\u6587\u7AE0\u5927\u6982\u662F\u5165\u4E0D\u4E86\u60A8\u7684\u6CD5\u773C\u7684\uFF0C\u66F4\u53EF\u80FD\u4F1A\u8BA9\u60A8\u751F\u4E00\u809A\u5B50\u95F2\u6C14\uFF0C\u90A3\u591A\u5212\u4E0D\u6765\u3002</p><h2 id="_1-3-\u90D1\u91CD\u58F0\u660E\u53CA\u5176\u4ED6\u58F0\u660E" tabindex="-1"><a class="header-anchor" href="#_1-3-\u90D1\u91CD\u58F0\u660E\u53CA\u5176\u4ED6\u58F0\u660E" aria-hidden="true">#</a> 1.3 \u90D1\u91CD\u58F0\u660E\u53CA\u5176\u4ED6\u58F0\u660E</h2><p>\u90D1\u91CD\u58F0\u660E\uFF1A</p><p>\u9119\u4EBA\u6280\u672F\u5947\u83DC\u65E0\u6BD4\uFF0C\u6545\u672C\u6587\u5FC5\u7136\u6302\u4E00\u6F0F\u4E07\u7834\u7EFD\u767E\u51FA\u3002\u60A8\u82E5\u53D1\u73B0\u95EE\u9898\u8FD8\u8BF7\u6E29\u67D4\u63D0\u9192\uFF0C\u83AB\u8981\u4EBA\u53C2\u516C\u9E21\u3002</p><p>\u514D\u8D23\u58F0\u660E\uFF1A</p><p>\u672C\u6587\u5185\u5BB9\u8BF7\u60A8\u81EA\u884C\u5224\u65AD\u662F\u5426\u53EF\u4FE1\u53EF\u9760\u53EF\u7528\uFF0C\u82E5\u60A8\u6839\u636E\u672C\u6587\u5185\u5BB9\u5EFA\u7ACB\u548C\u4F7F\u7528 VPS \u670D\u52A1\u5668\u65F6\u51FA\u4E86\u4EFB\u4F55\u95EE\u9898\u548C\u4E0D\u826F\u7ED3\u679C\uFF0C\u9119\u4EBA\u6982\u4E0D\u8D1F\u8D23\u3002</p><p>\u5570\u55E6\u58F0\u660E\uFF1A</p><p>\u57FA\u4E8E\u672C\u6587\u3010\u96F6\u57FA\u7840\u7528\u6237\u3011\u7684\u76EE\u6807\u53D7\u4F17\uFF0C\u8BB8\u591A\u5185\u5BB9\u4F1A\u5C3D\u529B\u8BE6\u5C3D\u8BF4\u660E\uFF0C\u6240\u4EE5\u8BED\u8A00\u504F\u5570\u55E6\uFF0C\u8BF7\u505A\u597D\u5FC3\u7406\u51C6\u5907\u3002</p><h2 id="_1-4-\u4E3A\u4EC0\u4E48\u81EA\u5EFA\u662F\u4E2A\u96BE\u9898" tabindex="-1"><a class="header-anchor" href="#_1-4-\u4E3A\u4EC0\u4E48\u81EA\u5EFA\u662F\u4E2A\u96BE\u9898" aria-hidden="true">#</a> 1.4 \u4E3A\u4EC0\u4E48\u81EA\u5EFA\u662F\u4E2A\u96BE\u9898\uFF1F</h2><p>\u8981\u56DE\u7B54\u8FD9\u4E2A\u95EE\u9898\uFF0C\u5C31\u9700\u8981\u7A0D\u5FAE\u591A\u8BF4\u4E00\u70B9\u80CC\u666F\u4FE1\u606F\u4E86\u3002</p><p>\u4E00\u3001\u79D1\u5B66\u4E0A\u7F51\u8FD9\u4EF6\u4E8B</p><p>\u79D1\u5B66\u4E0A\u7F51\u8FD9\u4EF6\u4E8B\u60C5\uFF0C\u8BF4\u6765\u5DF2\u7ECF\u53D1\u5C55\u4E86\u8FD1\u4E8C\u5341\u5E74\uFF08\u9707\u60CA!!!.jpg\uFF09\u3002\u6700\u521D\uFF0C\u81EA\u5DF1\u7A0D\u5FAE\u52A8\u52A8\u624B\u5373\u53EF\uFF08\u6539\u6539 host\u3001\u8FDE\u4E00\u4E0B ssh\uFF09\u3001\u540E\u6765\u9700\u8981\u627E\u4E00\u4E2A\u7F51\u9875\u4EE3\u7406\uFF0C\u518D\u540E\u6765\u9700\u8981\u5199\u4E00\u4E2A\u79C1\u6709\u534F\u8BAE(\u6BD4\u5982 Shadowsocks)\u7B49\u7B49\u3002</p><p>\u968F\u7740 GFW \u6280\u672F\u8FD9\u5341\u51E0\u5E74\u6765\u4E0D\u65AD\u7684\u8FED\u4EE3\u5347\u7EA7\uFF0C\u82E5\u8981\u5B8C\u6210\u3010\u81EA\u5DF1\u52A8\u624B\u79D1\u5B66\u4E0A\u7F51\u3011\u8FD9\u4E2A\u76EE\u6807\uFF0C\u9700\u8981\u505A\u7684\u4E8B\u60C5\u5DF2\u7ECF\u5305\u62EC\u4F46\u4E0D\u9650\u4E8E\uFF1A</p><ul><li>\u4E86\u89E3 Linux \u7CFB\u7EDF\u57FA\u672C\u547D\u4EE4</li><li>\u4E86\u89E3\u7F51\u7EDC\u4F20\u8F93\u534F\u8BAE</li><li>\u6709\u6280\u672F\u548C\u7ECF\u6D4E\u80FD\u529B\u5B8C\u6210 VPS \u8D2D\u4E70\u53CA\u7BA1\u7406</li><li>\u6709\u6280\u672F\u548C\u7ECF\u6D4E\u80FD\u529B\u5B8C\u6210\u57DF\u540D\u8D2D\u4E70\u53CA\u7BA1\u7406</li><li>\u6709\u6280\u672F\u80FD\u529B\u5B8C\u6210 TLS \u8BC1\u4E66\u7533\u8BF7 \u7B49\u7B49\u3002</li></ul><p>\u8FD9\u5C31\u8BA9\u3010\u81EA\u5EFA VPS \u79D1\u5B66\u4E0A\u7F51\u3011\u8FD9\u4E2A\u66FE\u7ECF\u7B80\u5355\u7684\u884C\u4E3A\u9010\u6E10\u53D8\u6210\u4E86\u4EE4\u65B0\u4EBA\u671B\u800C\u751F\u754F\u7684\u6311\u6218\u3002</p><p>\u4E8C\u3001\u96F6\u57FA\u7840\u7528\u6237\u7684\u65E0\u5948</p><p>\u96F6\u57FA\u7840\u7684\u975E\u6280\u672F\u7528\u6237\uFF0C\u5982\u679C\u5B8C\u6210\u4E0A\u9762\u8FD9\u4E00\u8FDE\u4E32\u7684\u64CD\u4F5C\uFF0C\u52BF\u5FC5\u8981\u5B66\u4E60\u5927\u91CF\u7684\u77E5\u8BC6\uFF0C\u4F46\u7A0D\u5FAE\u641C\u7D22\u4E4B\u540E\uFF0C\u65B0\u4EBA\u53EA\u6015\u4F1A\u66F4\u52A0\u8FF7\u832B\uFF1A\u5927\u91CF\u7684\u4FE1\u606F\u6563\u5E03\u5728\u4E92\u8054\u7F51\u7684\u5404\u4E2A\u89D2\u843D\uFF1A\u535A\u5BA2\u3001\u95EE\u7B54\u7F51\u7AD9\u3001\u7FA4\u7EC4\u3001\u8BBA\u575B\u3001GitHub\u3001Telegram\u3001YouTube \u7B49\u7B49\u7B49\u7B49\uFF09\u3002\u8FD9\u4E9B\u4FE1\u606F\u7EB7\u4E71\u590D\u6742\u3001\u6C34\u5E73\u826F\u83A0\u4E0D\u9F50\u3001\u751A\u81F3\u53EF\u80FD\u4E92\u76F8\u77DB\u76FE\u3002\u57FA\u672C\u4E0A\u5C31\u662F\u4E0D\u628A\u65B0\u4EBA\u5F7B\u5E95\u5F04\u6655\u8A93\u4E0D\u7F62\u4F11\u3002</p><p>\u9762\u5BF9\u8FD9\u4E9B\u6742\u4E71\u65E0\u7AE0\u7684\u4FE1\u606F\uFF0C\u65B0\u4EBA\u7A81\u7136\u5C31\u4ECE\u3010\u4FE1\u606F\u532E\u4E4F\u3011\u53D8\u6210\u4E86\u3010\u4FE1\u606F\u8FC7\u5269\u3011\u3002\u82E5\u662F\u51E0\u756A\u8FDE\u8499\u5E26\u731C\u7684\u6298\u817E\u4EE5\u5931\u8D25\u544A\u7EC8\uFF08\u5927\u6982\u7387\u5982\u6B64\uFF09\u7684\u8BDD\uFF0C\u4ED6\u7684\u79EF\u6781\u6027\u52BF\u5FC5\u5927\u53D7\u632B\u6298\u3002\u5728\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\uFF0C\u82E5\u4ED6\u53C8\u6070\u597D\u53BB\u4E86\u4E00\u4E9B\u4E0D\u592A\u53CB\u597D\u7684\u5730\u65B9\u53BB\u6C42\u52A9\uFF0C\u6050\u6015\u8FD8\u8981\u96EA\u4E0A\u52A0\u971C\u7684\u88AB\u5632\u8BBD\u4E00\u756A\uFF1A\u201C\u8FD9\u4E48\u83DC\uFF0C\u7528\u673A\u573A\u4E0D\u5C31\u884C\u4E86\uFF0C\u778E\u6298\u817E\u4EC0\u4E48\u554A\uFF01\u201D\u3001\u201C\u5148\u53BB\u5B66\u4F1A Linux \u518D\u56DE\u6765\u95EE\u5427\u201D\u3002</p><p>\u8FD9\u65F6\u5019\uFF0C\u5927\u6982\u4E5F\u53EA\u6709\u4E00\u58F0\u201C\u5475\u5475\u201D\u53EF\u4EE5\u8868\u8FBE\u5FC3\u60C5\u4E86\u3002</p><h2 id="_1-5-\u7528\u673A\u573A\u4E0D\u5C31\u884C\u4E86" tabindex="-1"><a class="header-anchor" href="#_1-5-\u7528\u673A\u573A\u4E0D\u5C31\u884C\u4E86" aria-hidden="true">#</a> 1.5 \u201C\u7528\u673A\u573A\u4E0D\u5C31\u884C\u4E86\uFF1F\u201D</h2><p>\u9996\u5148\uFF0C\u6211\u60F3\u53CD\u95EE\u4E00\u4E0B\u90A3\u4E9B\u51B7\u5632\u70ED\u8BBD\u7684\u4EBA\uFF1A\u201C\u7528\u673A\u573A\u201D\u771F\u7684\u5C31\u662F\u4E07\u7075\u836F\u5417\uFF1F</p><p>\u5176\u6B21\uFF0C\u6211\u8BA4\u4E3A\u201C\u4E0D\u61C2\u201D\u548C\u201C\u4E0D\u60F3\u61C2\u201D\u662F\u6709\u672C\u8D28\u533A\u522B\u7684\u3002\u6001\u5EA6\u6076\u52A3\u7684\u5DE8\u5A74\u4F38\u624B\u515A\u81EA\u7136\u60F9\u4EBA\u538C\u70E6\uFF0C\u4F46\u771F\u5FC3\u81EA\u5B66\u5374\u4E0D\u5F97\u8981\u9886\u7684\u4EBA\u4E0D\u8BE5\u53D7\u5230\u65E0\u7AEF\u7684\u767D\u773C\u548C\u6B67\u89C6\uFF0C\u4E5F\u6B63\u662F\u8FD9\u79CD\u5BF9\u65B0\u4EBA\u4E0D\u52A0\u533A\u5206\u7684\u6076\u52A3\u793E\u533A\u6C1B\u56F4\u4FC3\u4F7F\u6211\u5199\u4E0B\u672C\u6587\u3002\u90A3\u4E48\u95F2\u8BDD\u5C11\u8BF4\uFF0C\u6211\u4EEC\u6765\u770B\u770B\u673A\u573A\u7684\u4F18\u52BF\u4E0E\u52A3\u52BF\u7A76\u7ADF\u5982\u4F55\uFF1A</p><p>\u4E00\u3001\u201C\u673A\u573A\u201C\u7684\u4F18\u52BF</p><p>\u6240\u8C13\u201C\u673A\u573A\u201D\uFF0C\u5C31\u662F\u201C\u7EBF\u8DEF\u63D0\u4F9B\u5546\u201D\u3002\u4ED6\u8D1F\u8D23\u5B8C\u6210 1.4 \u63D0\u5230\u7684\u90A3\u4E00\u4E32\u6280\u672F\u64CD\u4F5C\u548C\u7BA1\u7406\uFF0C\u7528\u6237\u5219\u4ED8\u8D39\u83B7\u5F97\u4F7F\u7528\u6743\u3002\u6240\u4EE5\uFF0C\u5B83\u7684\u4F18\u70B9\u81F3\u5C11\u6709\uFF1A</p><ol><li><strong>\u7528\u6237\u64CD\u4F5C\u7B80\u5355</strong>\uFF1A\u626B\u7801\u64CD\u4F5C\u3001\u4E00\u952E\u6DFB\u52A0\u89C4\u5219\u7B49</li><li><strong>\u7EBF\u8DEF\u9009\u62E9\u591A</strong>\uFF1A\u53EF\u89E3\u9501\u4E0D\u540C\u56FD\u5BB6\u3001\u5730\u533A\u7684\u7F51\u7EDC\u670D\u52A1\uFF1B\u6BD4\u5982 iplc \u7B49\u4E13\u7EBF\u670D\u52A1\u3001\u6E38\u620F\u52A0\u901F\u670D\u52A1\u7B49</li><li><strong>\u63A5\u5165\u8282\u70B9\u591A</strong>\uFF1A\u6240\u4EE5\u62B5\u6297\u8282\u70B9\u5C01\u9501\u7684\u80FD\u529B\u5F3A\u4E00\u4E9B\uFF0C\u5C01\u4E86\u4E00\u4E2A\u5C31\u6362\u4E0B\u4E00\u4E2A</li></ol><p>\u4E8C\u3001\u201C\u673A\u573A\u201D\u7684\u98CE\u9669</p><p>\u201C\u65B9\u4FBF\u201D\u8FD9\u679A\u786C\u5E01\u7684\u53E6\u4E00\u9762\u5C31\u662F\u201C\u98CE\u9669\u201D\uFF0C\u57FA\u4E8E\u201C\u673A\u573A\u201D\u7684\u6280\u672F\u7279\u70B9\u548C\u5E02\u573A\u60C5\u51B5\uFF0C\u5B83\u7684\u98CE\u9669\u81F3\u5C11\u6709\uFF1A</p><ol><li><strong>\u201C\u673A\u573A\u201D\u53EF\u5B8C\u5168\u83B7\u5F97\u7528\u6237\u4FE1\u606F</strong>\uFF1A\u7528\u6237\u5728\u7F51\u4E0A\u7684\u6240\u6709\u75D5\u8FF9\uFF0C\u90FD\u3010\u5FC5\u7136\u3011\u7ECF\u8FC7\u4E14\u3010\u975E\u5E38\u53EF\u80FD\u3011\u957F\u671F\u5B58\u50A8\u5728\u5176\u670D\u52A1\u5668\u4E0A\uFF0C\u8FD9\u4E9B\u8BB0\u5F55\u65E0\u6CD5\u53D7\u5230\u4EFB\u4F55\u5177\u5907\u6CD5\u5F8B\u6548\u529B\u7684\u7528\u6237\u9690\u79C1\u534F\u8BAE\u7684\u7EA6\u675F\uFF08<strong>\u7AA5\u89C6\u3001\u8BB0\u5F55\u4F60\u7684\u4E00\u4E3E\u4E00\u52A8</strong>\uFF09</li><li><strong>\u201C\u673A\u573A\u201D\u7F3A\u4E4F\u5E02\u573A\u7BA1\u7406</strong>\uFF1A\u4E0D\u53EF\u907F\u514D\u5B58\u5728\u7740\u4EE5\u6B3A\u8BC8\u4E3A\u76EE\u6807\u7684\u6076\u610F\u5546\u5BB6\uFF08<strong>\u4E3B\u52A8\u8DD1\u8DEF</strong>\uFF09</li><li><strong>\u201C\u673A\u573A\u201D\u9762\u4E34\u76D1\u7BA1\u538B\u529B</strong>\uFF1A\u5927\u673A\u573A\u76F8\u5BF9\u6709\u4FDD\u969C\u7684\u540C\u65F6\uFF0C\u4E5F\u65E0\u6CD5\u907F\u514D\u6811\u5927\u62DB\u98CE\u30022020 \u5E74\u95F4\uFF0C\u5DF2\u7ECF\u6709\u51E0\u4E2A\u5927\u673A\u573A\u505C\u8FD0\u3001\u8DD1\u8DEF\u7684\u4E8B\u4EF6\u53D1\u751F\uFF0C\u7528\u6237\u7684\u6B63\u5E38\u4F7F\u7528\u53D7\u5230\u4E25\u91CD\u5E72\u6270\uFF08<strong>\u88AB\u52A8\u8DD1\u8DEF</strong>\uFF09</li><li><strong>\u201C\u673A\u573A\u201D\u6280\u672F\u6C34\u5E73\u96BE\u4EE5\u786E\u5B9A</strong>\uFF1A\u7EBF\u8DEF\u8D28\u91CF\u826F\u83A0\u4E0D\u9F50\uFF0C\u6302\u7F8A\u5934\u5356\u72D7\u8089\u7684\u73B0\u8C61\u5C61\u89C1\u4E0D\u9C9C\uFF08<strong>\u901F\u5EA6\u6162\u3001\u6389\u7EBF\u591A\u3001\u8FDE\u4E0D\u4E0A</strong>\uFF09</li></ol><h2 id="_1-6-\u90A3\u4E48\u4F60\u5230\u5E95\u8981\u4E0D\u8981\u81EA\u5EFA\u5462" tabindex="-1"><a class="header-anchor" href="#_1-6-\u90A3\u4E48\u4F60\u5230\u5E95\u8981\u4E0D\u8981\u81EA\u5EFA\u5462" aria-hidden="true">#</a> 1.6 \u90A3\u4E48\u4F60\u5230\u5E95\u8981\u4E0D\u8981\u81EA\u5EFA\u5462\uFF1F</h2><p>\u73B0\u5728\uFF0C\u4F60\u5DF2\u7ECF\u770B\u5230\u4E86\u673A\u573A\u7684\u4F18\u52BF\u548C\u98CE\u9669\uFF0C\u8981\u7528\u4EC0\u4E48\uFF0C\u5C31\u8BF7\u5404\u4F4D\u5145\u5206\u601D\u8003\u5E76\u81EA\u884C\u51B3\u5B9A\u3002\u6BD5\u7ADF\uFF0C\u6700\u9002\u5408\u4F60\u7684\u65B9\u6848\u624D\u662F\u6700\u597D\u7684\u65B9\u6848\u3002</p><p><img src="'+i+'" alt="It&#39;s Your Choice!"></p><ol><li><p>\u5982\u679C\u51B3\u5B9A\u4F7F\u7528\u673A\u573A\u7684\u8BDD\uFF0C\u73B0\u5728\uFF0C\u4F60\u53EF\u4EE5\u5173\u95ED\u672C\u6587\u4E86\u3002</p></li><li><p>\u5982\u679C\u4F60\u51B3\u5B9A\u81EA\u5EFA\uFF0C\u90A3\u5C31\u8BF7\u7EE7\u7EED\u9605\u8BFB\u540E\u9762\u7684\u7AE0\u8282\u5427\uFF01\uFF01</p></li></ol><p>\u603B\u4E4B\uFF0C\u672C\u6587\u7684\u76EE\u6807\u5C31\u662F\u6210\u4E3A\u96F6\u57FA\u7840\u7528\u6237\u7684\u77E5\u8BC6\u8D77\u70B9\uFF0C\u63D0\u4F9B\u5BF9\u6BCF\u4E00\u6B65\u5145\u5206\u7684\u8BB2\u89E3\u548C\u6F14\u793A\uFF0C\u6E05\u6E05\u695A\u695A\uFF08\u751A\u81F3<strong>\u5A46\u5A46\u5988\u5988\u3001\u7D6E\u7D6E\u53E8\u53E8\u3001\u5570\u5570\u55E6\u55E6</strong>\uFF09\u7684\u534F\u52A9\u65B0\u4EBA\u5B8C\u6210\u3010<strong>\u4ECE\u8F93\u5165\u7B2C\u4E00\u6761\u547D\u4EE4\u5F00\u59CB\uFF0C\u5B8C\u6210 VPS \u670D\u52A1\u5668\u90E8\u7F72\uFF0C\u5E76\u6210\u529F\u5728\u5BA2\u6237\u7AEF\u5B8C\u6210\u79D1\u5B66\u4E0A\u7F51</strong>\u3011\u7684\u5168\u7A0B\u3002\u5E76\u5728\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\u5E2E\u52A9\u65B0\u4EBA\u9010\u6B65\u63A5\u89E6\u548C\u719F\u6089 Linux \u7684\u57FA\u7840\u64CD\u4F5C\uFF0C\u4E3A\u4E4B\u540E\u7684\u8FDB\u4E00\u6B65\u81EA\u5B66\u6253\u4E0B\u57FA\u7840\u3002</p><h2 id="_1-7-\u9898\u5916\u5570\u55E6\u51E0\u53E5" tabindex="-1"><a class="header-anchor" href="#_1-7-\u9898\u5916\u5570\u55E6\u51E0\u53E5" aria-hidden="true">#</a> 1.7 \u9898\u5916\u5570\u55E6\u51E0\u53E5</h2><ol><li><p>\u5899\u5916\u7684\u4FE1\u606F\u6CE5\u6C99\u4FF1\u4E0B\uFF0C\u8BF7\u52A1\u5FC5\u5B66\u4F1A\u7406\u6027\u3001\u72EC\u7ACB\u7684\u601D\u8FA8\uFF0C\u4E0D\u8981\u968F\u610F\u7AD9\u961F\uFF0C\u4E0D\u8981\u8F7B\u4FE1\u730E\u5947\u7684\u4FE1\u606F\u3002</p></li><li><p>\u8877\u5FC3\u5E0C\u671B\u5927\u5BB6\u83B7\u5F97\u66F4\u987A\u7545\u7684\u7F51\u7EDC\u540E\uFF0C\u53EF\u4EE5\u83B7\u53D6\u66F4\u65B0\u9C9C\u7684\u77E5\u8BC6\u3001\u66F4\u4E30\u5BCC\u7684\u5A31\u4E50\u3001\u63A5\u89E6\u66F4\u7F8E\u597D\u7684\u4E16\u754C\u3001\u7ED3\u4EA4\u66F4\u591A\u5FD7\u540C\u9053\u5408\u7684\u670B\u53CB\uFF0C\u4F46\u4E0D\u8981\u6210\u4E3A\u4EFB\u4F55\u6709\u4E0D\u53EF\u544A\u4EBA\u76EE\u7684\u4E4B\u4EBA\u7684\u66FF\u7F6A\u7F8A\u3002</p></li><li><p>\u4F60\u7684\u4E92\u8054\u7F51\u8EAB\u4EFD\u4F9D\u7136\u662F\u4F60\u7684\u8EAB\u4EFD\uFF0C\u7EDD\u5BF9\u7684\u533F\u540D\u5316\u662F\u6781\u4E3A\u56F0\u96BE\u7684\uFF0C\u6240\u4EE5\u8BF7\u52A1\u5FC5\u9075\u5B88\u4F60\u4E2A\u4EBA\u6240\u5728\u5730\u533A\u548C IP \u6240\u5728\u5730\u533A\u7684\u76F8\u5173\u6CD5\u5F8B\u6CD5\u89C4\u3002\u65E0\u8BBA\u4F55\u65F6\uFF0C\u81EA\u6211\u4FDD\u62A4\u90FD\u662F\u6700\u57FA\u672C\u7684\u5E95\u7EBF\u3002</p></li></ol><h2 id="_1-8-\u4F60\u7684\u8FDB\u5EA6" tabindex="-1"><a class="header-anchor" href="#_1-8-\u4F60\u7684\u8FDB\u5EA6" aria-hidden="true">#</a> 1.8 \u4F60\u7684\u8FDB\u5EA6</h2><blockquote><p>\u2B1B\u2B1C\u2B1C\u2B1C\u2B1C\u2B1C\u2B1C\u2B1C 12.5%</p></blockquote>',41);function t(n,o){return p}var h=r(e,[["render",t],["__file","ch01-preface.html.vue"]]);export{h as default};
